import React, { useState, useEffect, useRef } from "react";
import "./App.css";

const DinoGame = () => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [score, setScore] = useState(0);
  const [gameSpeed, setGameSpeed] = useState(8);
  const [obstacleSpacing, setObstacleSpacing] = useState(250);
  const [gameOver, setGameOver] = useState(false);

  const dinoRef = useRef(5);


  const obstacleRef = useRef(null);

  const handleJump = () => {
    if (!isPlaying) {
      setIsPlaying(true);
    }
    dinoRef.current.classList.add("jump");
    setTimeout(() => {
      dinoRef.current.classList.remove("jump");
    }, 500);
  };

  const handleRestart = () => {
    setIsPlaying(false);
    setScore(0);
    setGameSpeed(8);
    setObstacleSpacing(250);
    setGameOver(false);
  };

  useEffect(() => {
    let gameLoop;
    let lastObstacle;

    const checkCollision = () => {
      const dino = dinoRef.current.getBoundingClientRect();
      const obstacle = obstacleRef.current.getBoundingClientRect();
      if (
        obstacle.left <= dino.right - 25 &&
        obstacle.right >= dino.left + 25 &&
        obstacle.top <= dino.bottom &&
        obstacle.bottom >= dino.top
      ) {
        setGameOver(true);
        cancelAnimationFrame(gameLoop);
      }
    };

    const gameStep = () => {
      setScore((prevScore) => prevScore + 1);
      setGameSpeed((prevSpeed) => prevSpeed + 0.05);
      setObstacleSpacing((prevSpacing) => prevSpacing - 0.5);
      checkCollision();
      gameLoop = requestAnimationFrame(gameStep);
    };

    if (isPlaying) {
      gameStep();
    }

    return () => cancelAnimationFrame(gameLoop);
  }, [isPlaying]);

  return (
    <div className="game-container">
      <div className="game-score">{gameOver ? "Game Over" : `Score: ${score}`}</div>
      {gameOver ? (
        <button onClick={handleRestart}>Restart</button>
      ) : (
        <div className="game-playarea" onClick={handleJump}>
          <div className="dino" ref={dinoRef} />

          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 1000 1000" stroke-width="1.5" stroke="currentColor" class="w-6 h-6">
          <path stroke-linecap="round" stroke-linejoin="round" d="M10.05 4.575a1.575 1.575 0 10-3.15 0v3m3.15-3v-1.5a1.575 1.575 0 013.15 0v1.5m-3.15 0l.075 5.925m3.075.75V4.575m0 0a1.575 1.575 0 013.15 0V15M6.9 7.575a1.575 1.575 0 10-3.15 0v8.175a6.75 6.75 0 006.75 6.75h2.018a5.25 5.25 0 003.712-1.538l1.732-1.732a5.25 5.25 0 001.538-3.712l.003-2.024a.668.668 0 01.198-.471 1.575 1.575 0 10-2.228-2.228 3.818 3.818 0 00-1.12 2.687M6.9 7.575V12m6.27 4.318A4.49 4.49 0 0116.35 15m.002 0h-.002" />
          </svg>

          <div className="obstacle" style={{ marginLeft: `${obstacleSpacing}px` }} ref={obstacleRef} />
        </div>
      )}
    </div>
  );
};

export default DinoGame;
